import React from 'react';
import TextField from '@material-ui/core/TextField';
import TextareaAutosize from '@material-ui/core/TextareaAutosize';
import ValidationAlert from '../ValidationAlert';

function TextareaInput({ name, test, type, value, alert, setInputs, placeholder, tester }) {
    return (
        <div className={test}>
            <TextareaAutosize id={name} name={name} aria-label="maximum height" value={value || ""} type={type} 
            onChange={setInputs} placeholder={placeholder} className={tester} row={4} rowMax={4} />
            <ValidationAlert content={alert} />
        </div>
    )
}

export default TextareaInput;
