import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import FormHelperText from '@material-ui/core/FormHelperText';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
import './styles/form.scss';

//像 class 一樣可存在多個於js檔裡
//適合客製化 / 增加不同元素

const inputStyles = makeStyles({
  root: {
    width: '100%',
    // '& .MuiInputLabel-shrink': {
    //   color: '#646a7a',
    //   fontFamily: '"Open Sans", sans-serif',
    //   fontSize: '16px',
    //   marginBottom: '10px',
    //   wordBreak: 'break-all',
    // },
    // '& .MuiFormLabel-root': {
    //   width: '100%',
    // },
    '.MuiInput-input': {
    },
  },
});

const selectStyle = makeStyles({
  root: {
    fontSize: '15px',
    color: '#222b45',
    fontWeight: '600',
    paddingTop: '14px',
    paddingBottom: '14px',
    border: '1px solid #c5cee0',
    backgroundColor: '#f7f9fc',
    boxSizing: 'border-box',
  },
});

const usStyles = makeStyles(theme => ({
  button: {
    backgroundColor: '#e9eee9',
    fontSize: '40px',
  },
}));

function Email() {
  const classes = [inputStyles(), usStyles()];
  return (
    <div>
      <FormHelperText>Your valid email address.</FormHelperText>
      <TextField id="Email" placeholder="Input mail address" 
      InputLabelProps={{ shrink: true }}
        className={classes.button } 
        helperText="Incorrect entry." 
        value={this.state.email.value}
        onChange={this.handleFieldChange} />
    </div>
  );
}

function CompanyName() {
  const classes = inputStyles();
  return (
    <div>
      <FormHelperText>Your registered company name. Example: "VASP LTD."</FormHelperText>
      <TextField id="CompanyName" placeholder="Your company name"
      InputLabelProps={{ shrink: true, }} classes={{
        root: classes.root, }} />
    </div>
  );
}

function VASPCode() {
  const classes = inputStyles();
  return (
    <div>
      <FormHelperText>
      4 uppercase letters to represent your company name. Example: "Coinbase exchange" could be "CBSE", "Sygna" could be "SYGN"
      </FormHelperText>
      <TextField id="VASPCodeName" placeholder="Input VASP code Name"
        InputLabelProps={{ shrink: true, }} 
        classes={{ root: classes.root, label: classes.label, }} />
    </div>
  );
}

function CountryCode() {
  const classes = selectStyle();
  const [CountryCode, setCountryCode] = React.useState('');
  const handleChange = (event) => {
    setCountryCode(event.target.value);
  };
  return (
    <FormControl variant="outlined">
      <FormHelperText>Country location of your company jurisdiction. Please follow ISO 3166-1 alpha-2 country code.</FormHelperText>
      <Select labelId="country-code-label" id="country-code" value={CountryCode} onChange={handleChange} displayEmpty 
        inputProps={{ 'aria-label': 'Without label' }}
        classes={{ root: classes.root, option: classes.option, }}>
        <MenuItem value="">Select</MenuItem>
        <MenuItem value={1}>Virtual/Flat</MenuItem>
        <MenuItem value={2}>Virtual/Virtual</MenuItem>
        <MenuItem value={3}>Transfer</MenuItem>
        <MenuItem value={4}>Custodian</MenuItem>
        <MenuItem value={5}>Other</MenuItem>
      </Select>
    </FormControl>
  );
}

function CityCode() {
  const classes = inputStyles();
  return (
    <div>
      <FormHelperText>2 letters city abbreviation. Example: "NY"</FormHelperText>
      <TextField id="CityCode" placeholder="Input city code"
        InputLabelProps={{ shrink: true, }} classes={{
        root: classes.root, label: classes.label, }} />
    </div>
  );
}

function CompanyID() {
  const classes = inputStyles();
  return (
    <div>
      <FormHelperText>Company registration code under local jurisdiction.</FormHelperText>
      <TextField id="CompanyID" placeholder="Input company ID"
      InputLabelProps={{ shrink: true, }} classes={{
        root: classes.root, label: classes.label, }} />
    </div>
  );
}

function VASPcatagory() {
  const classes = selectStyle();
  const [VASPcatagory, setVASPcatagory] = React.useState('');
  const handleChange = (event) => {
    setVASPcatagory(event.target.value);
  };
  return (
    <FormControl variant="outlined">
      <FormHelperText></FormHelperText>
      <Select labelId="vasp-catagory" id="vasp-catagory" value={VASPcatagory} onChange={handleChange} displayEmpty
      inputProps={{ 'aria-label': 'Without label' }} classes={{
        root: classes.root, label: classes.label,}} >
        <MenuItem value="">Select</MenuItem>
        <MenuItem value={1}>Virtual/Flat</MenuItem>
        <MenuItem value={2}>Virtual/Virtual</MenuItem>
        <MenuItem value={3}>Transfer</MenuItem>
        <MenuItem value={4}>Custodian</MenuItem>
        <MenuItem value={5}>Other</MenuItem>
      </Select>
    </FormControl>
  );
}

function VASPapi() {
  const classes = inputStyles();
  return (
    <div>
      <FormHelperText>
      API endpoint for beneficiary VASP to receive API request from Bridge Server (<a href="http://bit.ly/2NhZUlS" target="_blank">http://bit.ly/2NhZUlS</a>).
    Example: "<a href="#" target="_blank">beneficiary.example/api/v1/bridge/transaction/permission-request</a>
      </FormHelperText>
      <TextField id="VASPapi" placeholder="Input company ID"
      InputLabelProps={{ shrink: true, }} classes={{
        root: classes.root, label: classes.label, }} />
    </div>
  );
}

function IPAddress() {
  const classes = inputStyles();
  return (
    <div>
      <FormHelperText>
      To create a secure communication tunnel between Bridge and VASP servers, 
    Bridge can register VASP server static IP for identification. (optional)
      </FormHelperText>
      <TextField id="VIPAddress" placeholder="Input fixed static IP address"
      InputLabelProps={{ shrink: true, }} classes={{
        root: classes.root, label: classes.label, }} />
        <FormHelperText>
        Wrong
        </FormHelperText>
    </div>
  );
}

// function GoLiveDate() {
//   const [SelectedDate, setSelectedDate] = React.useState('');
//   const handleDateChange = (date) => {
//     setSelectedDate(date);
//   };
//   return (
//     <KeyboardDatePicker
//       disableToolbar
//       variant="inline"
//       format="MM/dd/yyyy"
//       margin="normal"
//       id="date-picker-inline"
//       label="Date picker inline"
//       value={SelectedDate}
//       onChange={handleDateChange}
//       KeyboardButtonProps={{
//         'aria-label': 'change date',
//       }}
//     />
//   );
// }

function VASPubKey() {
  const classes = selectStyle();
  const [VASPubKey, setVASPubKey] = React.useState('');
  const handleChange = (event) => {
    setVASPubKey(event.target.value);
  };
  return (
    <FormControl variant="outlined">
      <FormHelperText>Country location of your company jurisdiction. Please follow ISO 3166-1 alpha-2 country code.</FormHelperText>
      <Select labelId="vasp-pub-key-label" id="vasp-pub-key" value={VASPubKey} onChange={handleChange} displayEmpty
      inputProps={{ 'aria-label': 'Without label' }}
      classes={{ root: classes.root, option: classes.option, }}>
        <MenuItem value="">Select Public Type</MenuItem>
        <MenuItem value={1}>HEX</MenuItem>
        <MenuItem value={2}>PEM</MenuItem>
      </Select>
    </FormControl>
  );
}

function VASPubKeyType() {
  return (
    <TextField id="VASPubKeyType" multiline rows={4} fullWidth={true} 
    placeholder="'ex.\n-----BEGIN PUBLIC KEY-----\nMFYwEAYHKoZIzj0CAQYFK4EEAAoDQgAEKciXy+GOwFZ7JEt+kpcMpFdHkEpzG0ln\n9XIcNs+Ak5qykSXRF0YnzqgzWnXEg8wA/ZWkEw8V8oPPNd9v5PQynw==\n-----END PUBLIC KEY-----'" />
  );
}

function KeyPair() {
  return (
    <div className="info">
      <h6>Key pair generation</h6>
      <p>
        This short guide will help you to generate a key pair via the OpenSSL command line tool, in order to
        provide the public key to Bridge.
        <br /><br />
        Generate an EC private key, of size 256, and output it to a file named key.pem:
        >> $ openssl ecparam -name secp256k1 -genkey -out key.pem
        <br /><br />
        Extract the public key from the key pair, which can be used in the encryption:
        >> $ openssl ec -in key.pem -pubout -out public.pem
        <br /><br />
        After running these two commands you will generate two files: key.pem and public.pem in your working
        directory. Please provide us with the public.pem
        key.pem is referenced in the creation of a digital signature on the message. Please take care not to
        expose the private key. Ensure that you only do so on a system you consider to be secure.
      </p>
    </div>
  );
}

function CTA() {
  return (
    <div className="cta_block">
      <p className="text">
        If you have any questions with the information in this email, please let us know at
        <a href="mailto:contact@sygna.io">contact@sygna.io</a> or visit our website at <a href="sygna.io"
        target="_blank">sygna.io</a>
      </p>
      <div className="cta">
        <input type="submit" name="" value="Submit" className="btn btn-primary" />
      </div>
    </div>
  );
}


//什麼都不講的狀況下會選 default 引入，像 id 一樣只能存在一個於js檔裡
//適合做基礎樣式
export default function Header1() {
  return (
    <div className="container">
  <div className="intro">
    <h2 className="title">Welcome to Sygna Bridge VASP Registration</h2>
  </div>
  <form id="registration_form" Validate>
    <div className="input-field required">
      <h5>Email Address</h5>
      <Email></Email>
    </div>
    <div className="input-field required">
      <h5>Company Name</h5>
      <CompanyName></CompanyName>
    </div>
    <div className="input-field required">
      <h5>VASP Code Name</h5>
      <VASPCode></VASPCode>
    </div>
    <div className="input-field required">
      <h5>Country Code</h5>
      <CountryCode></CountryCode>
    </div>
    <div className="input-field required">
      <h5>City Code</h5>
      <CityCode></CityCode>
    </div>
    <div className="input-field">
      <h5>Company ID</h5>
      <CompanyID></CompanyID>
    </div>
    <div className="input-field required">
      <h5>VASP Category</h5>
      <VASPcatagory></VASPcatagory>
    </div>
    <div className="input-field required">
      <h5>Beneficiary VASP API endpoint URL</h5>
      <VASPapi></VASPapi>
    </div>
    <div className="input-field">
      <h5>Fixed static IP Address</h5>
      <IPAddress></IPAddress>
    </div>
    <div className="input-field">
      <h5>Go-Live Date</h5>
    </div>
    <div className="input-field required">
      <h5>VASP Public Key</h5>
      <VASPubKey></VASPubKey>
      <VASPubKeyType></VASPubKeyType>
    </div>
    <KeyPair />
    <CTA />
  </form>
</div>
  )
};

class Test extends React.Component {
  constructor() {
    super();

    this.formValidationRules = {
      email: [
        { rule: validationRules.required, message: "Email is required" },
        { rule: validationRules.email, message: "Email is invalid" }
      ]
    };

    this.fields = ["firstName", "lastName", "phone", "email"];

    this.state = {
      signupForm: { isValid: false },
      firstName: { value: "", isTouched: false, isValid: false, errors: [] },
      lastName: { value: "", isTouched: false, isValid: false, errors: [] },
      phone: { value: "234", isTouched: true, isValid: false, errors: [] },
      email: { value: "", isTouched: false, isValid: false, errors: [] }
    };
  }

  handleFieldChange = (e) => {
    let newState = { ...this.state };
    newState[e.target.name].value = e.target.value;
    this.validateForm(newState);
  };

  handleSetTouched = (e) => {
    let field = { ...this.state[e.target.name], isTouched: true };
    this.setState({ [e.target.name]: { ...field } });
  };

  getClassName = (fieldName) => {
    const field = this.state[fieldName];
    return field.isTouched && !field.isValid ? "has-error" : "";
  };

  validateForm = (newState) => {
    newState = newState || { ...this.state };
    this.fields.map((fieldName) => {
      let newField = newState[fieldName];
      newField.errors = [];
      newField.isValid = true;
      this.formValidationRules[fieldName].map((vRule) => {
        if (!vRule.rule(this.state[fieldName].value)) {
          newField.errors.push(vRule.message);
          newField.isValid = false;
        }
        newState[fieldName] = newField;
      });
    });
    this.setState(newState);
  };

  componentWillMount() {
    this.validateForm();
  }

  render() {
    const { email } = this.state;
    return (
      <form>
        <div className="field-group">
          <label>Email</label>
          <input
            className={email.isTouched && !email.isValid ? "has-error" : ""}
            name="email"
            value={this.state.email.value}
            onChange={this.handleFieldChange}
            onBlur={this.handleSetTouched}
          />
          {email.isTouched &&
            email.errors.length > 0 &&
            email.errors.map((err, i) => (
              <span key={i} className="error-message">
                {err}
              </span>
            ))}
        </div>
      </form>
    );
  }
}
