import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import FormHelperText from '@material-ui/core/FormHelperText';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
import './styles/form.scss';

// export function vasp_pub_key() {
//   const [VASPubKey, setVASPubKey] = React.useState('');
//   const handleChange = (event) => {
//     setVASPubKey(event.target.value);
//   };
//   return(
//     <div class="input-field">
//       <h5>VASP Public Key</h5>
//       <FormControl variant="outlined" className={classes.formControl}>
//         <InputLabel shrink id="vasp-pub-key-label">
//           For other VASPs to encrypt private information. Please see Key pair generation for more details.
//         </InputLabel>
//         <Select
//           labelId="vasp-pub-key-label"
//           id="vasp-pub-key"
//           value={VASPubKey}
//           onChange={handleChange}
//           displayEmpty
//           className={classes.selectEmpty}
//         >
//           <MenuItem value="">
//             <em>Select</em>
//           </MenuItem>
//           <MenuItem value={10}>Ten</MenuItem>
//           <MenuItem value={20}>Twenty</MenuItem>
//           <MenuItem value={30}>Thirty</MenuItem>
//         </Select>
//       </FormControl>
//     </div>
//   )
// };


//像 class 一樣可存在多個於js檔裡
//適合客製化 / 增加不同元素
function KeyPair() {
  return (
    <div className="info">
      <h6>Key pair generation</h6>
        <p>
                This short guide will help you to generate a key pair via the OpenSSL command line tool, in order to
                provide
                the public key to Bridge.
                <br/><br/>
                Generate an EC private key, of size 256, and output it to a file named key.pem:
                >> $ openssl ecparam -name secp256k1 -genkey -out key.pem
                <br/><br/>
                Extract the public key from the key pair, which can be used in the encryption:
                >> $ openssl ec -in key.pem -pubout -out public.pem
                <br/><br/>
                After running these two commands you will generate two files: key.pem and public.pem in your working
                directory. Please provide us with the public.pem
                key.pem is referenced in the creation of a digital signature on the message. Please take care not to
                expose
                the private key. Ensure that you only do so on a system you consider to be secure.
        </p>
    </div>
  );
}

function CTA() {
  return (
    <div className="cta_block">
      <p className="text">
        If you have any questions with the information in this email, please let us know at
        <a href="mailto:contact@sygna.io">contact@sygna.io</a> or visit our website at <a href="sygna.io" target="_blank">sygna.io</a>
      </p>
      <div className="cta">
        <input type="submit" name="" value="Submit" className="btn btn-primary" />
      </div>
    </div>
  );
}

const useStyles = makeStyles((theme) => ({
  root: {
    '& .MuiTextField-root': {
      //label: 
    },
    '& .MuiSelect-outlined & .MuiInputLabel-shrink': {
      transform: 'translate(0, -6px)',
    },
  },
  formControl: {
    width: '100%',
  },
  selectEmpty: {
    marginTop: '10px',
    height: '3rem',
    fontSize: '15px',
    color: '#222b45',
    fontWeight: '600',
    padding: '8px 16px 12px',
    borderRadius: '4px',
    border: '1px solid $g-blue',
    background: '#f7f9fc',
    boxSizing: 'border-box',
  },
  formLabel: {
    color: '#222b45',
    fontWeight: '600',
    letterSpacing: 'normal',
  },
  selectLabel: {
    transform: 'translate(0, -6px) scale(0.75) !important',
  },
  textField: {
    //marginLeft: theme.spacing(1),
    //marginRight: theme.spacing(1),
    //width: '25ch',
  },
}));

const selectStyle = {
  control: styles => ({ styles, backgroundColor: 'white' }),
  option: (styles, { data, isDisabled, isFocused, isSelected }) => {
    //const color = chroma(data.color);
    return {
      styles,
      backgroundColor: '#f7f9fc',
      color: '#FFF',
      cursor: 'default',
    };
  },
};

//什麼都不講的狀況下會選 default 引入，像 id 一樣只能存在一個於js檔裡
//適合做基礎樣式
export default function Header1() {
  const classes = useStyles();
  const [CountryCode, setCountryCode] = React.useState('');
  const [VASPubKey, setVASPubKey] = React.useState('');
  const handleChange = (event) => {
    setCountryCode(event.target.value);
    setVASPubKey(event.target.value);
  };
  return (
    <div className="container">
      <div className="intro">
        <h2 className="title">Welcome to Sygna Bridge VASP Registration</h2>
      </div>
      <form id="registration_form">
          <div className="input-field">
            <h5>Email Address</h5>
            <TextField
              id="standard-full-width"
              label="Your valid email address."
              placeholder="Input mail address"
              InputLabelProps={{
                shrink: true,
              }}
              className={classes.formLabel}
            />
          </div>
          {/* Country Code */}
          <div className="input-field">
            <h5>Country Code</h5>
            <FormControl variant="outlined" className={classes.formControl}>
              <InputLabel shrink id="country-code-label" className={classes.formLabel, classes.selectLabel}>
                Country location of your company jurisdiction. Please follow ISO 3166-1 alpha-2 country code.
              </InputLabel>
              <Select
                labelId="country-code-label"
                id="country-code"
                value={CountryCode}
                onChange={handleChange}
                displayEmpty
                styles={selectStyle}
              >
                <MenuItem value="">
                  <em>Select</em>
                </MenuItem>
                <MenuItem value={1}>Virtual/Flat</MenuItem>
                <MenuItem value={2}>Virtual/Virtual</MenuItem>
                <MenuItem value={3}>Transfer</MenuItem>
                <MenuItem value={4}>Custodian</MenuItem>
                <MenuItem value={5}>Other</MenuItem>
              </Select>
            </FormControl>
          </div>
          {/* VASP Public Key */}
          <div className="input-field">
            <h5>VASP Public Key</h5>
              <FormControl variant="outlined" className={classes.formControl}>
                <InputLabel shrink id="vasp-pub-key-label" className={classes.formLabel, classes.selectLabel}>
                  For other VASPs to encrypt private information. Please see Key pair generation for more details.
                </InputLabel>
                <Select
                  labelId="vasp-pub-key-label"
                  id="vasp-pub-key"
                  value={VASPubKey}
                  onChange={handleChange}
                  displayEmpty
                  className={classes.selectEmpty}
                >
                  <MenuItem value="">
                    <em>Select Public Type</em>
                  </MenuItem>
                  <MenuItem value={1}>HEX</MenuItem>
                  <MenuItem value={2}>PEM</MenuItem>
                </Select>
              </FormControl>
              <TextField
                id="standard-multiline-static"
                multiline
                rows={4}
                fullWidth
                placeholder="'ex.\n-----BEGIN PUBLIC KEY-----\nMFYwEAYHKoZIzj0CAQYFK4EEAAoDQgAEKciXy+GOwFZ7JEt+kpcMpFdHkEpzG0ln\n9XIcNs+Ak5qykSXRF0YnzqgzWnXEg8wA/ZWkEw8V8oPPNd9v5PQynw==\n-----END PUBLIC KEY-----'"
              />
          </div>
          <KeyPair />
          <CTA />
      </form>
    </div>
  );
}
