import 'date-fns';
import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import FormHelperText from '@material-ui/core/FormHelperText';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
import 'typeface-noto-sans';
import 'typeface-open-sans';
import DateFnsUtils from '@date-io/date-fns';
import {
  MuiPickersUtilsProvider,
  KeyboardTimePicker,
  KeyboardDatePicker,
} from '@material-ui/pickers';
import './styles/form.scss';
import Button from '@material-ui/core/Button';
/* eslint-enable */
import { ValidatorForm, TextValidator } from 'react-material-ui-form-validator';

//像 class 一樣可存在多個於js檔裡
//適合客製化 / 增加不同元素

const testStyles = makeStyles({
  root: {
    display: 'inline-block',
    width: '50%',
    backgroundColor: '#e9eee9',
    '.test': {
      backgroundColor: '#0404',
    },
  },
  test: {
    color: '#222b45',
  }
});

const inputStyles = makeStyles({
  root: {
    // width: '100%',
  },
  props: {
    // required: true,
    // Name of the component ⚛️
    MuiInputBase: {
      // The default props to change
      color: '#6600cc',
      required: true,
    },
  },
});

const selectStyle = makeStyles({
  root: {
    fontSize: '15px',
    color: '#222b45',
    fontWeight: '600',
    paddingTop: '14px',
    paddingBottom: '14px',
    border: '1px solid #c5cee0',
    backgroundColor: '#f7f9fc',
    boxSizing: 'border-box',
  },
});

const usStyles = makeStyles(theme => ({
  root: {
    button: {
      fontSize: '40px',
      backgroundColor: '#6600cc',
    },
  },
}));

const divStyle = {
  width: 'auto',
  fontSize: '15px',
    color: '#222b45',
    fontWeight: '600',
    paddingTop: '14px',
    paddingBottom: '14px',
    border: '1px solid #c5cee0',
    backgroundColor: '#f7f9fc',
    boxSizing: 'border-box',
};

// input
function Email() {
  const classes = [inputStyles(), usStyles()];
  return (
    <div>
      <FormHelperText>Your valid email address.</FormHelperText>
      <TextField id="Email" placeholder="Input mail address" 
      InputLabelProps={{ shrink: true }}
      type="mail" style={divStyle}
        helperText="Incorrect entry." />
    </div>
  );
}
// select
function CountryCode() {
  const classes = selectStyle();
  const [CountryCode, setCountryCode] = React.useState('');
  const handleChange = (event) => {
    setCountryCode(event.target.value);
  };
  return (
    <FormControl variant="outlined">
      <FormHelperText>Country location of your company jurisdiction. Please follow ISO 3166-1 alpha-2 country code.</FormHelperText>
      <Select labelId="country-code-label" id="country-code" value={CountryCode} onChange={handleChange} displayEmpty 
        inputProps={{ 'aria-label': 'Without label' }}
        classes={{ root: classes.root, option: classes.option, }}>
        <MenuItem value="">Select</MenuItem>
        <MenuItem value={1}>Virtual/Flat</MenuItem>
        <MenuItem value={2}>Virtual/Virtual</MenuItem>
        <MenuItem value={3}>Transfer</MenuItem>
        <MenuItem value={4}>Custodian</MenuItem>
        <MenuItem value={5}>Other</MenuItem>
      </Select>
    </FormControl>
  );
}

function KeyPair() {
  return (
    <div className="info">
      <h6>Key pair generation</h6>
      <p>
        This short guide will help you to generate a key pair via the OpenSSL command line tool, in order to
        provide the public key to Bridge.
        <br /><br />
        Generate an EC private key, of size 256, and output it to a file named key.pem:
        >> $ openssl ecparam -name secp256k1 -genkey -out key.pem
        <br /><br />
        Extract the public key from the key pair, which can be used in the encryption:
        >> $ openssl ec -in key.pem -pubout -out public.pem
        <br /><br />
        After running these two commands you will generate two files: key.pem and public.pem in your working
        directory. Please provide us with the public.pem
        key.pem is referenced in the creation of a digital signature on the message. Please take care not to
        expose the private key. Ensure that you only do so on a system you consider to be secure.
      </p>
    </div>
  );
}

function CTA() {
  return (
    <div className="cta_block">
      <p className="text">
        If you have any questions with the information in this email, please let us know at <a href="mailto:contact@sygna.io">contact@sygna.io</a> or visit our website at <a href="sygna.io"
        target="_blank">sygna.io</a>
      </p>
      <div className="cta">
        <input type="submit" name="" value="Submit" className="btn btn-primary" />
      </div>
    </div>
  );
}


//什麼都不講的狀況下會選 default 引入，像 id 一樣只能存在於一個js檔裡
//適合做基礎樣式
export default class Header1 extends React.Component {
  state = {
    formData: {
        email: '',
        companyname: '',
        vaspcode: '',
        countrycode: '',
        vaspapi: '',
    },
    submitted: false,
  }

  handleChange = (event) => {
    const { formData } = this.state;
    formData[event.target.name] = event.target.value;
    this.setState({ formData });
  }

  handleSubmit = () => {
    this.setState({ submitted: true }, () => {
        setTimeout(() => this.setState({ submitted: false }), 5000);
    });
  }
  render() {
    const { formData, submitted } = this.state;
    return (
      <div className="container">
        <div className="intro">
          <h2 className="title">Welcome to Sygna Bridge VASP Registration</h2>
        </div>
        <ValidatorForm id="registration_form"
              ref="form"
              onSubmit={this.handleSubmit}
        > 
          <div className="input-field required">
            <h5>Email Address</h5>
            <FormHelperText>Your valid email address.</FormHelperText>
            <TextValidator id="Email" placeholder="Input mail address" 
              InputLabelProps={{ shrink: true }}
              type="mail" 
              onChange={this.handleChange}
              name="email"
              value={formData.email}
              validators={['required', 'isEmail']}
              errorMessages={['This field is required', 'Email is not valid']} 
            />
          </div>
          <div className="input-field required">
            <h5>Company Name</h5>
            <FormHelperText>Your registered company name. Example: "VASP LTD."</FormHelperText>
            <TextValidator id="CompanyName" placeholder="Your company name" 
              InputLabelProps={{ shrink: true }}
              type="text"
              onChange={this.handleChange}
              name="companyname"
              value={formData.companyname}
              validators={['required', 'isString']}
              errorMessages={['This field is required', 'Company name is not valid']} 
            />
          </div>
          <div className="input-field required">
            <h5>VASP Code Name</h5>
            <FormHelperText>4 uppercase letters to represent your company name. Example: "Coinbase exchange" could be "CBSE", "Sygna" could be "SYGN"</FormHelperText>
            <TextValidator id="VASPCodeName" placeholder="Input VASP code Name"
              InputLabelProps={{ shrink: true }}
              type="text" 
              onChange={this.handleChange}
              name="vaspcode"
              value={formData.vaspcode}
              validators={['required', 'isString']}
              errorMessages={['This field is required', 'VASP code name is not valid']} 
            />
          </div>
          <div className="input-field required">
            <h5>Country Code</h5>
            <FormControl variant="outlined">
              <FormHelperText>Country location of your company jurisdiction. Please follow ISO 3166-1 alpha-2 country code.</FormHelperText>
              <TextValidator select id="CountryCode" value={formData.countrycode.value} onChange={this.handleChange}  
                inputProps={{ 'aria-label': 'Without label' }} className="selectStyle" placeholder={formData.countrycode.test}
              >
                <MenuItem test>Select</MenuItem>
                <MenuItem value={1}>Virtual/Flat</MenuItem>
                <MenuItem value={2}>Virtual/Virtual</MenuItem>
                <MenuItem value={3}>Transfer</MenuItem>
                <MenuItem value={4}>Custodian</MenuItem>
                <MenuItem value={5}>Other</MenuItem>
              </TextValidator>
            </FormControl>
          </div>
          <div className="input-field required">
            <h5>City Code</h5>
            <FormHelperText>2 letters city abbreviation. Example: "NY"</FormHelperText>
            <TextValidator id="CityCode" placeholder="Input city code"
              InputLabelProps={{ shrink: true }}
              type="text" 
              onChange={this.handleChange}
              name="citycode"
              value={formData.citypcode}
              validators={['required', 'isString', 'minStringLength: 2', 'maxStringLength: 2']}
              errorMessages={['This field is required', 'City code is not valid']} 
            />
          </div>
          <div className="input-field">
            <h5>Company ID</h5>
            <FormHelperText>Company registration code under local jurisdiction.</FormHelperText>
            <TextValidator id="CompanyID" placeholder="Input company ID"
              InputLabelProps={{ shrink: true }}
              type="text" 
              onChange={this.handleChange}
              name="company_id"
              value={formData.companyid}
            />
          </div>
          <div className="input-field required">
            <h5>Beneficiary VASP API endpoint URL</h5>
            <FormHelperText>API endpoint for beneficiary VASP to receive API request from Bridge Server (<a href="http://bit.ly/2NhZUlS" target="_blank">http://bit.ly/2NhZUlS</a>).
    Example: "<a href="#" target="_blank">beneficiary.example/api/v1/bridge/transaction/permission-request</a></FormHelperText>
            <TextValidator id="VASPapi" placeholder="Input beneficiary VASP API endpoint URL"
              InputLabelProps={{ shrink: true }}
              type="text" 
              onChange={this.handleChange}
              name="vaspapi"
              value={formData.vaspapi}
            />
          </div>
          <div className="input-field">
            <h5>Fixed static IP Address</h5>
            <FormHelperText>To create a secure communication tunnel between Bridge and VASP servers, Bridge can register VASP server static IP for identification. (optional)</FormHelperText>
            <TextValidator id="VIPAddress" placeholder="Input fixed static IP address"
              InputLabelProps={{ shrink: true }}
              type="text" 
              onChange={this.handleChange}
              name="vip_address"
              value={formData.vip_address}
            />
          </div>
          <div className="input-field">
            <h5>Go-Live Date</h5>
            <FormHelperText>Default date: three days after the registration is done.</FormHelperText>
            <TextValidator id="go_live_date" placeholder="YY/MM/DD 00:00"
              InputLabelProps={{ shrink: true }}
              type="text" 
              onChange={this.handleChange}
              name="go_live_date"
            />
          </div>
          <KeyPair />
          <CTA />
        </ValidatorForm>
      </div>
    )
  }
};
