import React from 'react';
import ValidationAlert from '../ValidationAlert';

function TextareaInput({ name, test, type, value, alert, setInputs, placeholder }) {
    return (
        <div className={test}>
            <textarea id={name} name={name} value={value || ""} type={type} 
            onChange={setInputs} placeholder={placeholder}></textarea>
            <ValidationAlert content={alert} />
        </div>
    )
}

export default TextareaInput;
