import React from 'react';
import ValidationAlert from '../ValidationAlert';

function TextareaInput({ name, type, value, alert, setInputs }) {
    return (
        <div className={test}>
            <textarea id={name} name={name} value={value || ""} type={type} onChange={setInputs} className="textarea"></textarea>
            <ValidationAlert content={alert} />
        </div>
    )
}

export default TextareaInput;
