import React from 'react';
import MenuItem from '@material-ui/core/MenuItem';
import FormHelperText from '@material-ui/core/FormHelperText';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
import ValidationAlert from '../ValidationAlert';

{/* <div className={test}>
    <h5>{name}</h5>
    <FormHelperText>{label}</FormHelperText>
    <TextField id={name} name={name} type={type} value={value || ""} 
    onChange={setInputs} placeholder={placeholder} />
    <ValidationAlert content={alert} />
</div>

<div>
    <label htmlFor={name} className="uk-form-label uk-text-large">{label}</label>
    <select id={name} name={name} type={type} value={value} onChange={setInputs} className={"uk-select" + (alert ? ' uk-form-danger' : '')}>
        {options && options.map(option => <option key={option.value} value={option.value}>{option.name}</option>)}
    </select>
    <ValidationAlert content={alert} />
</div> */}

function SelectInput({ name, test, label, type, value, alert, options, setInputs }) {
    return (
        <div className={test}>
            <FormControl variant="outlined">
                <FormHelperText>{label}</FormHelperText>
                <Select id={name} name={name} type={type} value={value} onChange={setInputs} >
                    {options && options.map(option => <MenuItem key={option.value} value={option.value}>{option.name}</MenuItem>)}
                </Select>
            </FormControl>
            <ValidationAlert content={alert} />
        </div>
    )
}

export default SelectInput;
