import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
import TextField from '@material-ui/core/TextField';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
import MenuItem from '@material-ui/core/MenuItem';
import 'typeface-noto-sans';
import 'typeface-open-sans';

const useStyles = makeStyles((theme) => ({
    my_1: {
      marginTop: theme.spacing(1),
      marginBottom: theme.spacing(1),
    },
    paper: {
      padding: theme.spacing(4),
    },
    capitalize: {
      textTransform: 'capitalize',
    },
}));

export default function TransInfo () {
    const classes = useStyles();
    const margin = {
        margin: '30px 0 0'
    }
    const [CountryCode, setCountryCode] = React.useState('');
    const handleChange = (event) => {
      setCountryCode(event.target.value);
    };
    return (
        <React.Fragment>
            <Grid container spacing={3} className={classes.my_1}>
                <Grid item xs={12} sm={6}>
                    <FormControl fullWidth>
                        <Select id="country-code" 
                        value={CountryCode} onChange={handleChange} displayEmpty 
                        inputProps={{ 'aria-label': 'Without label' }}>
                            <MenuItem value="" disabled>Select</MenuItem>
                            <MenuItem value={1}>BTC</MenuItem>
                            <MenuItem value={2}>ETH</MenuItem>
                        </Select>
                    </FormControl>
                </Grid>
            </Grid>
            <Grid container spacing={3}>
                <Grid item xs={12} sm={6}>
                    <TextField
                        required
                        id="amount"
                        name="amount"
                        fullWidth
                        placeholder="ex: 0.347895"
                    />
                </Grid>
            </Grid>
            <div style={margin}>
                <Typography variant="h6" color="inherit" className={classes.capitalize}>
                    beneficiary info
                </Typography>
                <span>Your registered company name. Example: "VASP LTD."</span>
            </div>
            <Grid container spacing={3} className={classes.my_1}>
                <Grid item xs={12} sm={6}>
                    <TextField
                        required
                        id="amount"
                        name="amount"
                        fullWidth
                        placeholder="ex: 0.347895"
                    />
                </Grid>
                <Grid item xs={12} sm={6}>
                    <FormControl fullWidth>
                        <Select id="country-code" 
                        value={CountryCode} onChange={handleChange} displayEmpty 
                        inputProps={{ 'aria-label': 'Without label' }}>
                            <MenuItem value="" disabled>Select</MenuItem>
                            <MenuItem value={1}>BTC</MenuItem>
                            <MenuItem value={2}>ETH</MenuItem>
                        </Select>
                    </FormControl>
                </Grid>
                <Grid item xs={12}>
                    <TextField
                        required
                        id="amount"
                        name="amount"
                        fullWidth
                        placeholder="ex: 0.347895"
                    />
                </Grid>
            </Grid>
        </React.Fragment>
    );
}
